  /*
   * Main Structure
   */
  container packet-discard-reporting {
    description
      "Container for packet discard reporting data.";

    list interface {
      key "name";
      description
        "List of interfaces for which packet discard reporting data is provided.";

      leaf name {
        type string;
        description
          "Name of the interface.";
      }

      uses interface;
    }
  }
